    private void btnAprobadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAprobadosActionPerformed
        // TODO add your handling code here:
        Pila pilAux = new Pila(pil.getTamanio());
        String r = "";
        Notas not;

        String codigoM = txtCodigoMateria.getText();
        while (!pil.Vacia()) {
            not = (Notas) pil.Desapilar();
            pilAux.Apilar(not);
            if (not.getCodMateria().equals(codigoM)) {
                if (not.getNota1() + not.getNota2() >= 14
                        || not.getNota1() + not.getNota2() + not.getNotaSup() >= 21) {
                    r += not.getCedula() + " " + not.getCodMateria() + " " + not.getNota1()
                            + " " + not.getNota2() + " " + not.getNotaSup() + "\n";
                }
            }
        }

        txaResultado.setText(r);
        while (!pilAux.Vacia()) {
            pil.Apilar(pilAux.Desapilar());
        }
    }//GEN-LAST:event_btnAprobadosActionPerformed

