    private void btnNumReprobadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNumReprobadosActionPerformed
        // TODO add your handling code here:
        Pila pilAux = new Pila(pil.getTamanio());
        String r = "";
        Notas not;
        int i = 0;
        String codigoM = txtCodigoMateria.getText();
        while (!pil.Vacia()) {
            not = (Notas) pil.Desapilar();
            pilAux.Apilar(not);
            if (not.getCodMateria().equals(codigoM)) {
                if (not.getNota1() + not.getNota2() < 11
                        || not.getNota1() + not.getNota2() + not.getNotaSup() < 21) {
                    i++;
                }
            }
        }
        r += "Reprobados: " + i;
        txaResultado.setText(r);
        while (!pilAux.Vacia()) {
            pil.Apilar(pilAux.Desapilar());
        }
    }//GEN-LAST:event_btnNumReprobadosActionPerformed

    private void btnProomedioCursoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProomedioCursoActionPerformed
        // TODO add your handling code here:
        try {
            Pila pAux = new Pila(pil.getTamanio());
            Notas not;
            int contEstudiantes = 0;
            String resultado = "";
            HashMap<String, Integer> aprobadosPorMateria = new HashMap<String, Integer>();
            HashMap<String, Double> promedioPorMateria = new HashMap<String, Double>();

            while (!pil.Vacia()) {
                contEstudiantes++;
                not = (Notas) pil.Desapilar();
                pAux.Apilar(not);
                double promedioEstudiante = (not.getNota1() + not.getNota2()) / 2.0;
                double sumaPromediosMateria = promedioPorMateria.getOrDefault(not.getCodMateria(), 0.0);
                sumaPromediosMateria += promedioEstudiante;
                promedioPorMateria.put(not.getCodMateria(), sumaPromediosMateria);
                int aprobadosEnMateria = aprobadosPorMateria.getOrDefault(not.getCodMateria(), 0);
                aprobadosPorMateria.put(not.getCodMateria(), aprobadosEnMateria + 1);
            }

            String resultadoMaterias = "";

            for (Map.Entry<String, Integer> entry : aprobadosPorMateria.entrySet()) {
                String codMateria = entry.getKey();
                int numEstudiantes = entry.getValue();
                double sumaPromedios = promedioPorMateria.get(codMateria);
                double promedioMateria = sumaPromedios / numEstudiantes;
                resultadoMaterias += "Promedio de la Materia " + codMateria + ": " + promedioMateria + " (" + numEstudiantes + " estudiantes)\n";
            }
            if (contEstudiantes > 0) {
                txaResultado.setText("El promedio total por materia: \n" + resultado + "\n" + resultadoMaterias);
            } else {
                txaResultado.setText("Pila vacia");
            }

            while (!pAux.Vacia()) {
                not = (Notas) pAux.Desapilar();
                pil.Apilar(not);
            }
        } catch (Exception e) {
            txaResultado.setText("Pila vacia");
        }
    }//GEN-LAST:event_btnProomedioCursoActionPerformed

