    private void btnTransformarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTransformarActionPerformed
        // TODO add your handling code here:
        t = new TransfInfPost(this.txtExpInfija.getText());
        t.ConvertirArregloInfijo();
        t.TransformarPostfijo();
        this.txtExpPostfija.setText(t.RetornarExpPostfija());
    }//GEN-LAST:event_btnTransformarActionPerformed

    private void btnEvaluarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEvaluarActionPerformed
        // TODO add your handling code here:
      
        int resultado = t.TransformarExpresionPosfija();
        this.txtResultado.setText(Integer.toString(resultado));

    }//GEN-LAST:event_btnEvaluarActionPerformed

